import 'package:any_link_preview/any_link_preview.dart';
import 'package:chatify/chatify.dart';
import 'package:chatify/src/localization/get_string.dart';
import 'package:chatify/src/ui/chat_view/message/message_card.dart';
import 'package:chatify/src/ui/common/circular_loading.dart';
import 'package:chatify/src/ui/chat_view/controllers/chat_controller.dart';
import 'package:chatify/src/ui/chat_view/message/widgets/bubble.dart';
import 'package:chatify/src/ui/chat_view/message/widgets/send_at.dart';
import 'package:chatify/src/ui/common/kr_builder.dart';
import 'package:chatify/src/utils/extensions.dart';
import 'package:flutter/material.dart';

final Map<String, Message> _cache = {};

class TextMessageCard extends StatefulWidget {
  const TextMessageCard({
    super.key,
    required this.widget,
    required this.bkColor,
    required this.textColor,
    required this.controller,
    required this.isMine,
    required this.isSending,
    required this.isSelected,
  });

  final Color bkColor;
  final Color textColor;
  final bool isMine;
  final MessageCardWidget widget;
  final ChatController controller;
  final bool isSending;
  final bool isSelected;

  @override
  State<TextMessageCard> createState() => _TextMessageCardState();
}

class _TextMessageCardState extends State<TextMessageCard> {
  @override
  Widget build(BuildContext context) {
    final message = widget.widget.message.message(localization(context));
    final hasLink = message.urls.length > 1;
    final isOnlyEmojis = containsOnlyEmojis(message);

    final mergeWithSendAt = message.length < 35 &&
        !widget.widget.message.message(localization(context)).contains('\n') &&
        !hasLink &&
        !isOnlyEmojis;
    if (isOnlyEmojis)
      return Column(
        crossAxisAlignment: CrossAxisAlignment.start,
        children: [
          _MessageContent(
            widget: widget,
            mergeWithSendAt: mergeWithSendAt,
            hasLink: hasLink,
            isOnlyEmojis: isOnlyEmojis,
          ),
          Container(
            margin: const EdgeInsetsDirectional.only(end: 16, start: 16),
            child: Container(
              padding: const EdgeInsets.symmetric(horizontal: 6, vertical: 2),
              decoration: BoxDecoration(
                color: widget.bkColor,
                borderRadius: BorderRadius.circular(16),
              ),
              child: Row(
                mainAxisSize: MainAxisSize.min,
                children: widget.widget.message.emojis
                    .map(
                      (e) => Text(
                        e.emoji,
                        style: const TextStyle(height: 1.3),
                      ),
                    )
                    .toList(),
              ),
            ),
          ),
        ],
      );
    return Directionality(
      textDirection: TextDirection.ltr,
      child: Stack(
        alignment: AlignmentDirectional.bottomEnd,
        children: [
          Padding(
            padding: EdgeInsets.only(
              bottom: widget.widget.message.emojis.isEmpty ? 0 : 14,
            ),
            child: MyBubble(
              message: widget.widget.message,
              bkColor: widget.bkColor,
              linkedWithBottom: widget.widget.linkedWithBottom,
              linkedWithTop: widget.widget.linkedWithTop,
              child: _MessageContent(
                widget: widget,
                mergeWithSendAt: mergeWithSendAt,
                hasLink: hasLink,
                isOnlyEmojis: isOnlyEmojis,
              ),
            ),
          ),
          if (widget.widget.message.emojis.isNotEmpty)
            Container(
              margin: const EdgeInsetsDirectional.only(end: 10),
              padding: const EdgeInsets.all(1),
              decoration: BoxDecoration(
                color: Theme.of(context).scaffoldBackgroundColor,
                borderRadius: BorderRadius.circular(16),
              ),
              child: Container(
                padding: const EdgeInsets.symmetric(horizontal: 6),
                decoration: BoxDecoration(
                  color: widget.bkColor.withOpacity(widget.isMine ? 0.2 : 1),
                  borderRadius: BorderRadius.circular(16),
                ),
                child: Row(
                  mainAxisSize: MainAxisSize.min,
                  children: widget.widget.message.emojis
                      .map(
                        (e) => Text(
                          e.emoji,
                          style: const TextStyle(height: 1.3),
                        ),
                      )
                      .toList(),
                ),
              ),
            ),
        ],
      ),
    );
  }
}

class _MessageContent extends StatelessWidget {
  const _MessageContent({
    required this.widget,
    required this.mergeWithSendAt,
    required this.hasLink,
    required this.isOnlyEmojis,
  });

  final TextMessageCard widget;
  final bool mergeWithSendAt;
  final bool hasLink;
  final bool isOnlyEmojis;

  @override
  Widget build(BuildContext context) {
    return Directionality(
      textDirection: TextDirection.ltr,
      child: Padding(
        padding: EdgeInsets.only(
          right: 12,
          left: 12,
          top: 6,
          bottom: 6,
        ),
        child: Column(
          crossAxisAlignment: CrossAxisAlignment.start,
          children: [
            if (widget.widget.message.replyId != null)
              Padding(
                padding: const EdgeInsets.only(bottom: 5),
                child: SizedBox(
                  height: 37,
                  child: Row(
                    mainAxisSize: MainAxisSize.min,
                    children: [
                      Container(
                        height: 27,
                        width: 2,
                        decoration: BoxDecoration(
                          color: widget.isMine
                              ? Colors.white70
                              : Chatify.theme.primaryColor,
                          borderRadius: BorderRadius.circular(4),
                        ),
                      ),
                      const SizedBox(
                        width: 10,
                      ),
                      Flexible(
                        child: Column(
                          crossAxisAlignment: CrossAxisAlignment.start,
                          children: [
                            Text(
                              widget.widget.message.replyUid ==
                                      Chatify.currentUserId
                                  ? 'Me'
                                  : widget.widget.users
                                      .firstWhere(
                                        (e) =>
                                            e.id ==
                                            widget.widget.message.replyUid,
                                      )
                                      .name,
                              style: TextStyle(
                                color: widget.textColor.withOpacity(0.8),
                                fontSize: 14,
                              ),
                            ),
                            Expanded(
                              child: KrFutureBuilder<Message?>(
                                future: Chatify.datasource.readMessage(
                                  widget.widget.message.replyId!,
                                ),
                                initialData:
                                    _cache[widget.widget.message.replyId!],
                                onEmpty: Text(
                                  localization(context).deletedMessage,
                                  style: TextStyle(
                                    color: widget.textColor.withOpacity(0.8),
                                    fontSize: 12,
                                  ),
                                  maxLines: 1,
                                  overflow: TextOverflow.ellipsis,
                                ),
                                onError: (_) => Text(
                                  localization(context).deletedMessage,
                                  style: TextStyle(
                                    color: widget.textColor.withOpacity(0.8),
                                    fontSize: 12,
                                  ),
                                  maxLines: 1,
                                  overflow: TextOverflow.ellipsis,
                                ),
                                onLoading: widget.widget.message.replyMessage !=
                                        null
                                    ? Text(
                                        widget.widget.message.replyMessage!,
                                        style: TextStyle(
                                          color:
                                              widget.textColor.withOpacity(0.8),
                                          fontSize: 12,
                                        ),
                                        maxLines: 1,
                                        overflow: TextOverflow.ellipsis,
                                      )
                                    : SizedBox(
                                        width: 20,
                                        child: Center(
                                          child: LoadingWidget(
                                            size: 10,
                                            lineWidth: 1,
                                            color: widget.isMine
                                                ? Colors.white
                                                : Chatify.theme.primaryColor,
                                          ),
                                        ),
                                      ),
                                builder: (message) {
                                  _cache.putIfAbsent(
                                    widget.widget.message.replyId!,
                                    () => message!,
                                  );
                                  return Text(
                                    message?.message(
                                          localization(
                                            context,
                                          ),
                                        ) ??
                                        '',
                                    style: TextStyle(
                                      color: widget.textColor.withOpacity(0.8),
                                      fontSize: 12,
                                    ),
                                    maxLines: 1,
                                    overflow: TextOverflow.ellipsis,
                                  );
                                },
                              ),
                            ),
                          ],
                        ),
                      ),
                    ],
                  ),
                ),
              ),
            if (widget.widget.users.length > 2 &&
                !widget.isMine &&
                !widget.widget.linkedWithTop)
              Text(
                widget.widget.users
                    .firstWhere(
                      (e) => e.id == widget.widget.message.sender,
                    )
                    .name,
                style: TextStyle(
                  color: Chatify.theme.primaryColor,
                  fontSize: 14,
                ),
              ),
            Row(
              crossAxisAlignment: CrossAxisAlignment.end,
              mainAxisSize: MainAxisSize.min,
              children: [
                Flexible(
                  child: Padding(
                    padding: const EdgeInsets.only(bottom: 2),
                    child: Wrap(
                      children: widget.widget.message
                          .message(localization(context))
                          .urls
                          .map(
                            (e) => Text(
                              e,
                              textAlign: TextAlign.right,
                              style: TextStyle(
                                decoration:
                                    e.isURL ? TextDecoration.underline : null,
                                decorationColor: widget.textColor,
                                color: widget.textColor,
                                fontSize: isOnlyEmojis ? 30 : null,
                              ),
                            ),
                          )
                          .toList(),
                    ),
                  ),
                ),
                if (mergeWithSendAt)
                  SendAtWidget(
                    message: widget.widget.message,
                    isSending: widget.isSending,
                    iconColor: isOnlyEmojis
                        ? ChatifyTheme.of(context).primaryColor
                        : null,
                    textColor: isOnlyEmojis
                        ? ChatifyTheme.of(context).chatForegroundColor
                        : null,
                  ),
              ],
            ),
            if (hasLink)
              Stack(
                children: [
                  Positioned.fill(
                    child: Align(
                      alignment: Alignment.centerLeft,
                      child: LayoutBuilder(
                        builder: (context, size) {
                          return Container(
                            width: 2,
                            margin: EdgeInsetsDirectional.symmetric(
                              vertical: size.maxHeight > 20 ? 10 : 0,
                            ),
                            color: widget.textColor,
                          );
                        },
                      ),
                    ),
                  ),
                  Padding(
                    padding: const EdgeInsetsDirectional.only(start: 10),
                    child: AnyLinkPreview(
                      link: widget.widget.message
                          .message(localization(context))
                          .urls
                          .firstWhere((e) => e.isURL)
                          .urlFormat,
                      displayDirection: UIDirection.uiDirectionVertical,
                      showMultimedia: true,
                      bodyMaxLines: 5,
                      bodyTextOverflow: TextOverflow.ellipsis,
                      titleStyle: TextStyle(
                        color: widget.textColor,
                        fontWeight: FontWeight.bold,
                        fontSize: 15,
                      ),
                      bodyStyle: TextStyle(
                        color: widget.textColor.withOpacity(0.8),
                        fontSize: 12,
                      ),
                      errorWidget: SizedBox.shrink(),
                      placeholderWidget: SizedBox.shrink(),
                      cache: Duration(days: 360),
                      backgroundColor: Colors.transparent,
                      removeElevation: true,
                      previewHeight: 200,
                    ),
                  ),
                ],
              ),
            if (!mergeWithSendAt)
              SendAtWidget(
                message: widget.widget.message,
                isSending: widget.isSending,
                iconColor:
                    isOnlyEmojis ? ChatifyTheme.of(context).primaryColor : null,
                textColor: isOnlyEmojis
                    ? ChatifyTheme.of(context).chatForegroundColor
                    : null,
              ),
          ],
        ),
      ),
    );
  }
}

bool containsOnlyEmojis(String text) {
  final emojiRegex = RegExp(
      r'[#*0-9]\uFE0F?\u20E3|[\xA9\xAE\u203C\u2049\u2122\u2139\u2194-\u2199\u21A9\u21AA\u231A\u231B\u2328\u23CF\u23ED-\u23EF\u23F1\u23F2\u23F8-\u23FA\u24C2\u25AA\u25AB\u25B6\u25C0\u25FB\u25FC\u25FE\u2600-\u2604\u260E\u2611\u2614\u2615\u2618\u2620\u2622\u2623\u2626\u262A\u262E\u262F\u2638-\u263A\u2640\u2642\u2648-\u2653\u265F\u2660\u2663\u2665\u2666\u2668\u267B\u267E\u267F\u2692\u2694-\u2697\u2699\u269B\u269C\u26A0\u26A7\u26AA\u26B0\u26B1\u26BD\u26BE\u26C4\u26C8\u26CF\u26D1\u26D3\u26E9\u26F0-\u26F5\u26F7\u26F8\u26FA\u2702\u2708\u2709\u270F\u2712\u2714\u2716\u271D\u2721\u2733\u2734\u2744\u2747\u2757\u2763\u27A1\u2934\u2935\u2B05-\u2B07\u2B1B\u2B1C\u2B55\u3030\u303D\u3297\u3299]\uFE0F?|[\u261D\u270C\u270D](?:\uFE0F|\uD83C[\uDFFB-\uDFFF])?|[\u270A\u270B](?:\uD83C[\uDFFB-\uDFFF])?|[\u23E9-\u23EC\u23F0\u23F3\u25FD\u2693\u26A1\u26AB\u26C5\u26CE\u26D4\u26EA\u26FD\u2705\u2728\u274C\u274E\u2753-\u2755\u2795-\u2797\u27B0\u27BF\u2B50]|\u26F9(?:\uFE0F|\uD83C[\uDFFB-\uDFFF])?(?:\u200D[\u2640\u2642]\uFE0F?)?|\u2764\uFE0F?(?:\u200D(?:\uD83D\uDD25|\uD83E\uDE79))?|\uD83C(?:[\uDC04\uDD70\uDD71\uDD7E\uDD7F\uDE02\uDE37\uDF21\uDF24-\uDF2C\uDF36\uDF7D\uDF96\uDF97\uDF99-\uDF9B\uDF9E\uDF9F\uDFCD\uDFCE\uDFD4-\uDFDF\uDFF5\uDFF7]\uFE0F?|[\uDF85\uDFC2\uDFC7](?:\uD83C[\uDFFB-\uDFFF])?|[\uDFC3\uDFC4\uDFCA](?:\uD83C[\uDFFB-\uDFFF])?(?:\u200D[\u2640\u2642]\uFE0F?)?|[\uDFCB\uDFCC](?:\uFE0F|\uD83C[\uDFFB-\uDFFF])?(?:\u200D[\u2640\u2642]\uFE0F?)?|[\uDCCF\uDD8E\uDD91-\uDD9A\uDE01\uDE1A\uDE2F\uDE32-\uDE36\uDE38-\uDE3A\uDE50\uDE51\uDF00-\uDF20\uDF2D-\uDF35\uDF37-\uDF7C\uDF7E-\uDF84\uDF86-\uDF93\uDFA0-\uDFC1\uDFC5\uDFC6\uDFC8\uDFC9\uDFCF-\uDFD3\uDFE0-\uDFF0\uDFF8-\uDFFF]|\uDDE6\uD83C[\uDDE8-\uDDEC\uDDEE\uDDF1\uDDF2\uDDF4\uDDF6-\uDDFA\uDDFC\uDDFD\uDDFF]|\uDDE7\uD83C[\uDDE6\uDDE7\uDDE9-\uDDEF\uDDF1-\uDDF4\uDDF6-\uDDF9\uDDFB\uDDFC\uDDFE\uDDFF]|\uDDE8\uD83C[\uDDE6\uDDE8\uDDE9\uDDEB-\uDDEE\uDDF0-\uDDF5\uDDF7\uDDFA-\uDDFF]|\uDDE9\uD83C[\uDDEA\uDDEC\uDDEF\uDDF0\uDDF2\uDDF4\uDDFF]|\uDDEA\uD83C[\uDDE6\uDDE8\uDDEA\